package com.samsung.android.app.foodnoteimport com.samsung.android.sdk.healthdata.HealthConstantsimport java.util.concurrent.TimeUnitobject AppConstants {    const val BUNDLE_KEY_INTAKE_DAY = "INDEX_TIME"    const val BUNDLE_KEY_MEAL_TYPE = "MEAL_TYPE"    @JvmField    val BREAKFAST_TIME = TimeUnit.HOURS.toMillis(8)    @JvmField    val LUNCH_TIME = TimeUnit.HOURS.toMillis(12)    @JvmField    val DINNER_TIME = TimeUnit.HOURS.toMillis(18)    @JvmField    val MORNING_SNACK_TIME = TimeUnit.HOURS.toMillis(10)    @JvmField    val AFTERNOON_SNACK_TIME = TimeUnit.HOURS.toMillis(16)    @JvmField    val EVENING_SNACK_TIME = TimeUnit.HOURS.toMillis(22)    fun getMealTypeName(mealType: Int): String {        val mealTypeName: String        mealTypeName = when (mealType) {            HealthConstants.Nutrition.MEAL_TYPE_BREAKFAST -> "Breakfast"            HealthConstants.Nutrition.MEAL_TYPE_LUNCH -> "Lunch"            HealthConstants.Nutrition.MEAL_TYPE_DINNER -> "Dinner"            HealthConstants.Nutrition.MEAL_TYPE_MORNING_SNACK -> "Morning Snack"            HealthConstants.Nutrition.MEAL_TYPE_AFTERNOON_SNACK -> "Afternoon Snack"            HealthConstants.Nutrition.MEAL_TYPE_EVENING_SNACK -> "Evening Snack"            else -> "Unknown"        }        return mealTypeName    }}